import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
//other way to have multiple useStates
  //   const [enteredTitle, setEnteredTitle] = useState("");
  //   const titleChangeHandler = (event) => {
  //     return setEnteredTitle(event.target.value);
  //   };
  //   const [enteredAmount, setEnteredAmount] = useState("");
  //   const amountChangeHandler = (event) => {
  //     return setEnteredAmount(event.target.value);
  //   };
  //   const [enteredDate, setEnteredDate] = useState("");
  //   const dateChangeHandler = (event) => {
  //     return setEnteredDate(event.target.value);
  //   };
  const [userInput, setUserInput] = useState({
    enteredTitle: "",
    enteredAmount: "",
    enteredDate: "",
  });
  const titleChangeHandler = (event) => {
    setUserInput((prevState) => {
        return {...prevState, enteredTitle: event.target.value};
    });
  };
  const amountChangeHandler = (event) => {
    setUserInput((prevState) => {
        return{...prevState, enteredAmount: event.target.value}
    });
  };
  const dateChangeHandler = (event) => {
    setUserInput((prevState) => {
        return {...prevState, enteredDate: event.target.value};
    });
  };

  const submitHandler = () => {

  };

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChage={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2019-01-01"
            max="2022-12-31"
            onChange={dateChangeHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
